import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { MensajeService } from 'src/app/services/mensaje.service';
import { UsuarioService } from 'src/app/services/usuario.service';

@Component({
  selector: 'app-chat',
  templateUrl: './chat.component.html',
  styleUrls: ['./chat.component.css']
})
export class ChatComponent implements OnInit {

  constructor(private router:Router,
              private usuarioService:UsuarioService,
              private mensajeService:MensajeService) { }

  ngOnInit(): void {
    this.getSalaDates()
    setTimeout(() => {
      this.obaqui()
      console.log("cargando... otra vez");
    }, 3000);
    this.llenarMensajes()
    /* this.getUsuarioChateando() */
  }
    /* getUsuarioChateando(){

       let usuarioChat =   JSON.parse(localStorage.getItem("usuariochat"))
    console.log(usuarioChat);

  } */
sala:any
getSalaDates(){
  let salaDates =  JSON.parse(localStorage.getItem('salachat')) || '{}'
  this.sala = salaDates[0];
  console.log(this.sala);
}
async getUsuarioChateando(){
  return new Promise((resolve, reject) => {
    let usuarioChat = JSON.parse(localStorage.getItem("usuariochat"))
    if (usuarioChat) {
      resolve(usuarioChat)
    } else {
      reject(new Error('No se ha encontrado el usuario en el local storage'))
    }
  })
}
obaqui(){
  this.getUsuarioChateando().then((usuarioChat:any) => {
    this.usuarioService.getUsuarioByIdentificador(usuarioChat.identificadorusuario).subscribe((u:any)=>{
      localStorage.setItem("usuariochat",JSON.stringify(u));
    })
  }).catch((error:any) => {
    console.error(error)
  })
}
listaMensajes = []
llenarMensajes(){
  this.mensajeService.getMensajesByIdSala(this.sala.idsala).subscribe((mensajes:any)=>{
    for(let m of mensajes){
      this.listaMensajes.push(m)
    }
  })
}
mensajeEnviar = ""
enviarMensaje(){
  console.log(mensajeEnviar);

}
  direccionarUrl(donde:string){
    this.router.navigate([donde])
  }
}
